CREATE PROCEDURE pedido_perdido   @numero_de_seguimiento INTEGER ASBEGIN   WITH cliente_a_contactar AS   (SELECT idCliente FROM orden WHERE noSeguimiento=@numero_de_seguimiento)   SELECT DISTINCT correo_electronico, numero_telefonico FROM cliente_virtual INNER JOIN cliente_a_contactar ON cliente_virtual.idCliente = cliente_a_contactar.idCliente;   INSERT INTO orden (idCliente, nombreRemitente, empresaEnvio, direccionEnvio, noSeguimiento) SELECT idCliente, nombreRemitente, empresaEnvio, direccionEnvio, noSeguimiento FROM orden WHERE noSeguimiento=@numero_de_seguimiento; END
